mapper.select:
SELECT JMLM.MEMBER_PK, JMLM.ENTERED_DATE, JMLM.EXPERIENCE_YEARS, JMLM.IS_ACTIVE AS 'MEMBER_ACTIVE', JMLM.MEMBER_ID, JMLM.NAME AS 'MEMBER_NAME', JMLM.COMPANY_ID, JMLM.DEPARTMENT_ID, JOM.ORGANIZATION_PK, JOM.COMPANY_NAME, JOM.DEPARTMENT_NAME, JOM.IS_ACTIVE AS 'ORG_ACTIVE', JOM.PARENET_DEPARTMENT_ID, JOM.PARENET_DEPARTMENT_NAME FROM JXX_MEMBER_LEAVE_MASTER JMLM JOIN JXX_ORGANIZATION_MASTER JOM ON JMLM.COMPANY_ID = JOM.COMPANY_ID AND JMLM.DEPARTMENT_ID = JOM.DEPARTMENT_ID
AND JMLM.MEMBER_ID = #{memberId}
AND JMLM.NAME = #{memberName}
AND JMLM.COMPANY_ID = #{companyId}
AND JMLM.DEPARTMENT_ID = #{departmentId}
AND JMLM.IS_ACTIVE = #{memberActive}
AND JOM.IS_ACTIVE = #{orgActive}
