mapper.select:
SELECT V.VACATION_ID, V.REQUESTER_ID, ML.NAME, O.COMPANY_NAME, O.COMPANY_ID, O.DEPARTMENT_NAME, O.DEPARTMENT_ID, VD.START_DATE_TIME, VD.END_DATE_TIME, V.VACATION_TYPE, V.VACATION_STATUS FROM JXX_MEMBER_LEAVE_MASTER AS ML JOIN JXX_VACATION_MASTER AS V ON ML.MEMBER_ID = V.REQUESTER_ID JOIN JXX_ORGANIZATION_MASTER AS O ON ML.COMPANY_ID = O.COMPANY_ID AND ML.DEPARTMENT_ID = O.DEPARTMENT_ID JOIN JXX_VACATION_DURATION VD ON VD.VACATION_ID = V.VACATION_ID
AND ML.COMPANY_ID =#{companyId}
AND ML.DEPARTMENT_ID =#{departmentId}
AND V.VACATION_STATUS IN
#{vacationStatus}
